<?php/** * Render template with passed data * @param $template_path * @param $data * @return bool|string */function renderTemplate($template_path, $data = array()){  try {    // Check if template file exists    if (!is_readable($template_path)) {      return '';    }    $result = '';    // Start buffer and import passed data array elements into the symbol table    ob_start() && extract($data, EXTR_SKIP);    //get template content from file    $content = file_get_contents($template_path);    print($content);    // Get result from buffer and clean buffer    $result = ob_get_clean();    return $result;  } catch (Exception $e) {    return $e->getMessage();  }}